import React, { useState } from "react";
import { Link, useLocation } from "react-router-dom";
import { useAuth } from "../services/useAuth.tsx";
import { ResultData } from "shared/dist/types";

const ResultView: React.FC = () => {
  const [isViewLoaded, setIsLoaded] = useState<boolean>(false);

  const { user } = useAuth();
  const myId = user?.uid || "error";

  // Location and Params
  const location = useLocation();
  const result: ResultData = location.state.resultData;

  const handleGoHome = () => {
    console.log("Navigating to HomeView...");
    // Implement navigation logic here
  };

  return (
    <div>
      <h2>Game Result</h2>
      <p>
        <strong>Player ID:</strong> {result.playerId}
      </p>
      <p>
        <strong>Score:</strong> {result.score}
      </p>
      <p>
        <strong>Time Taken:</strong> {result.time} ms
      </p>
      <p>
        <strong>Result:</strong> {result.win === "win" ? "Victory" : "Defeat"}
      </p>

      <h3>Correct Answers</h3>
      <ul>
        {result.corrects.map((correct, index) => (
          <li key={index}>
            Question {index + 1}: {correct ? "Correct" : "Incorrect"}
          </li>
        ))}
      </ul>

      <h3>My Answer</h3>
      <p>
        <strong>Identity:</strong> {result.myAnswer.identity ? "Yes" : "No"}
      </p>
      <p>
        <strong>Message:</strong> {result.myAnswer.message || "No message"}
      </p>
      <p>
        <strong>Selection:</strong> {result.myAnswer.select ? "True" : "False"}
      </p>

      <h3>Opponent's Answer</h3>
      <p>
        <strong>Identity:</strong>{" "}
        {result.opponentAnswer.identity ? "Yes" : "No"}
      </p>
      <p>
        <strong>Message:</strong>{" "}
        {result.opponentAnswer.message || "No message"}
      </p>
      <p>
        <strong>Selection:</strong>{" "}
        {result.opponentAnswer.select ? "True" : "False"}
      </p>
    </div>
  );
};

export default ResultView;
