import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import {
  requestMatch,
  onMatchFound,
  savePlayerName,
} from "../services/FRD.ts";
import { useAuth } from "../services/useAuth.tsx"; // useAuthフックをインポート
import { signOut } from "firebase/auth"; // Firebaseのログアウト機能をインポート
import { auth } from "../services/firebase.ts"; // Firebaseの認証インスタンスをインポート

const HomeView: React.FC = () => {
  const [playerName, setPlayerName] = useState<string>("");
  const [playerScore, setPlayerScore] = useState<number>(9999);
  const [aiPrompt, setAiPrompt] = useState<string>("Input AI prompt here");
  const [isMatching, setIsMatching] = useState<boolean>(false);
  const navigate = useNavigate();

  const { user } = useAuth(); // useAuthフックで認証状態を取得

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (currentUser) => {
      if (!currentUser) {
        // ユーザーがログアウトしている場合、ログイン画面にリダイレクト
        navigate("/login");
      }
    });

    // クリーンアップ関数でリスナーを解除
    return () => unsubscribe();
  }, [navigate]);

  const handleLogout = async () => {
    try {
      await signOut(auth);
      alert("ログアウトしました");
      navigate("/login"); // ログアウト後にログイン画面にリダイレクト
    } catch (error) {
      console.error("ログアウトエラー:", error);
    }
  };

  const startMatch = (): void => {
    const nameToSave = playerName === "" ? "ゲスト" : playerName;
    setPlayerName(nameToSave);
    savePlayerName(nameToSave);
    setIsMatching(true);
    requestMatch();

    // マッチング成功時にバトル画面へ遷移
    onMatchFound((data) => {
      console.log("Match found with opponent:", data.opponentId);
      navigate(`/battle/${data.roomId}`, {
        state: { matchData: data, myData: { playerName: nameToSave, playerScore, aiPrompt } },
      });
      setIsMatching(false);
    });
  };

  return (
    <div>
      <h1>ホーム</h1>
      {user && <p>こんにちは、{user.email}さん</p>} {/* ログイン中のユーザーのメールアドレスを表示 */}

      <div>
        <button onClick={handleLogout}>ログアウト</button> {/* ログアウトボタン */}
      </div>

      <p>PlayerName: {playerName}</p>
      <input
        type="text"
        placeholder="Enter your name"
        value={playerName}
        onChange={(e) => setPlayerName(e.target.value)}
      />
      <p>Score: {playerScore}</p>
      <div>
        <label>
          AIプロンプト:
          <input
            type="text"
            value={aiPrompt}
            onChange={(e) => setAiPrompt(e.target.value)}
          />
        </label>
      </div>

      <button onClick={startMatch} disabled={isMatching}>
        {isMatching ? "Matching..." : "Start Matching"}
      </button>
    </div>
  );
};

export default HomeView;
